import React from "react";
import "../styles/MenuBar.css";
import userPic from "../assets/myPic.jpg";
import Content from "./Content";
import { useState } from "react";

const MenuBar = () => {
  const [isStyles, setIsStyles] = useState(false);
  const [isPhotoStyles, setIsPhotoStyles] = useState(false);

  const toggleStyle = () => {
    setIsStyles(!isStyles);
  }
  const toggleStylePhoto = () => {
    setIsPhotoStyles(!isPhotoStyles);
  }
  const photoStyles = {
    borderLeft: isPhotoStyles && '10px solid transparent',
    borderRight: isPhotoStyles && '10px solid transparent',
    borderBottom: isPhotoStyles && '10px solid transparent',
  }
  return (
    <div className="menu-container">
      <header>
        <img src={userPic} alt="userImg" onClick={toggleStylePhoto} style={photoStyles} />
        <ul className="menu-bar">
          <li>
            <a href="#about" target="_self">
              ABOUT
            </a>
          </li>
          <li>
            <a href="#exp" target="_self">
              EXPERIENCE
            </a>
          </li>
          <li>
            <a href="#educate" target="_self">
              EDUCATION
            </a>
          </li>
          <li>
            <a href="#skill" target="_self">
              SKILLS
            </a>
          </li>
          <li onClick={toggleStyle}>
            <span>SET MODE</span>
          </li>
        </ul>
      </header>
      <Content isStyles={isStyles} />
    </div>
  );
};

export default MenuBar;
